import { Component } from '@angular/core';
import { Data, Router } from '@angular/router';
import { RecipeService } from '../recipe.service';
import { KrogerService } from '../kroger.service';
import { Datum, KrogerProduct } from '../kroger';


@Component({
  selector: 'app-shopping-list',
  templateUrl: './shopping-list.component.html',
  styleUrls: ['./shopping-list.component.css']
})
export class ShoppingListComponent {
  currentShoppingList:string[]= [];
  krogerSearchResults:Datum[][] = [];


  constructor(private recipeService:RecipeService, private krogerService:KrogerService, 
    private router:Router) {

  }

  ngOnInit(){
    this.currentShoppingList = this.krogerService.shoppingList;
    this.krogerSearchResults = this.krogerService.productsToShop;
  }

  clearShoppingList():void{
    let emptyStringArray:string[]=[];
    this.krogerService.shoppingList = emptyStringArray; 
  }


  goToRecipe():void{
    this.router.navigate(["recipe"]);
  }


}
<p>shopping-list works!</p>


<div class="table-container">
  <div class="table-horizontal-container">
    <table class="unfixed-table">
      <tbody>
        <tr *ngFor="let c of currentShoppingList; let i=index">
          <th>{{c}}</th>
          <span *ngFor="let data of krogerSearchResults[i]; let j=index">
            <!-- <td *ngFor="let d of data; let k=index">
              {{d.brand}}
            </td> -->
            <td>{{data.brand}}</td>
          </span>
          <!-- <td>Column three</td>
          <td>Column four</td>
          <td>Column firve</td>
          <td>Column six</td>
          <td>Column seven</td>
          <td>Column eight</td> -->
        </tr>
      </tbody>
    </table>
  </div>
</div>
<!-- </object>
</article> -->


<button (click)="goToRecipe()">Return to recipe</button>




